    private void buttonZwiekszActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonZwiekszActionPerformed
        int coZmieniamy = sprawdzComboBox();
        Integer dzien = waliduj(textFieldDzien);
        Integer miesiac = waliduj(textFieldMiesiac);
        boolean czy31 = checkBx31dni.isSelected();
        switch (coZmieniamy) {
            case 1:

                if (czy31) {
                    dzien++;
                    if (dzien > 31) {
                        dzien = 1;
                        miesiac++;
                    }
                } else {
                    dzien++;
                    if (dzien > 30) {
                        dzien = 1;
                        miesiac++;
                    }
                }

                break;
            case 2:
                break;
            case 3:
                break;
        }

        textFieldDzien.setText(String.valueOf(dzien));
        textFieldMiesiac.setText(String.valueOf(miesiac));
        wpisz();
    }//GEN-LAST:event_buttonZwiekszActionPerformed

