    private void wpisz() {
        String data = textFieldDzien.getText() + "/" + textFieldMiesiac.getText() + "/" + textFieldRok.getText();
        textFieldData.setText(data);
    }

    private Integer waliduj(javax.swing.JTextField textField) {
        Integer liczba = 0;
        try {
            liczba = Integer.valueOf(textField.getText());
        } catch (NumberFormatException e) {
            textField.setText("0");
            e.printStackTrace();
        }
        return liczba;
    }

    private int sprawdzComboBox() {
        int coSprawdzamy = 0;
        String co = (String) comboBoxWartosc.getSelectedItem();
        switch (co) {
            case "dzien":
                coSprawdzamy = 1;
                break;
            case "miesiÄ…c":
                coSprawdzamy = 2;
                break;
            case "rok":
                coSprawdzamy = 3;
                break;
        }
        return coSprawdzamy;
    }
